# --- Script to find applied Conditional Access policies for a device ---

# 1. Set the User and Device variables
# !!! IMPORTANT: Replace "user@lin46.onmicrosoft.com" with the actual User Principal Name (UPN) of the user who signed in from the device.
$userUPN = "AD97@lin46.myo365.net"
$entraDeviceID = "52db0d4e-be75-4a4e-b12c-4e9c9422a46d"

# 2. Retrieve the latest sign-in log for that user and device
Write-Host "Searching for the latest sign-in from '$userUPN' on device '$entraDeviceID'..."
$signInLog = Get-MgAuditLogSignIn -Filter "userPrincipalName eq '$userUPN' and deviceDetail/deviceId eq '$entraDeviceID'" -Top 1 -Sort "createdDateTime DESC"

# 3. Display the results
if ($null -ne $signInLog) {
    Write-Host "--------------------------------------------------------" -ForegroundColor Green
    Write-Host "Sign-in Found:" -ForegroundColor Green
    Write-Host "User: $($signInLog.UserPrincipalName)"
    Write-Host "Time: $($signInLog.CreatedDateTime)"
    Write-Host "Application: $($signInLog.AppDisplayName)"
    Write-Host "Sign-in Result: $($signInLog.Status.AdditionalDetails)"
    Write-Host "--------------------------------------------------------" -ForegroundColor Green
    Write-Host "Applied Conditional Access Policies:" -ForegroundColor Yellow

    # Loop through and display each applied policy's details
    $policies = $signInLog.ConditionalAccessPolicies
    if ($policies.Count -gt 0) {
        $policies | ForEach-Object {
            [PSCustomObject]@{
                PolicyName   = $_.DisplayName
                Result       = $_.Result
                EnforcedBy   = if ($_.EnforcedBy) { $_.EnforcedBy } else { 'Conditional Access' }
                GrantControls= if ($_.GrantControls) { ($_.GrantControls.BuiltInControls -join ', ') } else { 'N/A' }
            } | Format-Table -AutoSize
        }
    } else {
        Write-Host "No Conditional Access policies were applied to this sign-in." -ForegroundColor Cyan
    }

} else {
    Write-Host "--------------------------------------------------------" -ForegroundColor Red
    Write-Host "No recent sign-in logs found for the specified user and device." -ForegroundColor Red
    Write-Host "Please check that the UPN and Device ID are correct and that a sign-in has occurred recently."
}

# Disconnect from Microsoft Graph
# Disconnect-MgGraph

